var plan = require('./lib');

plan.target('test', {
  host: 'Jookbox.me',
  username: 'pstadler',
  agent: process.env.SSH_AUTH_SOCK
});

plan.remote(function(remote) {
  remote.sudo('echo hello world', { user: 'node' });
});

plan.remote(function(remote) {
  remote.sudo('printf "hello world"', { user: 'node' });
  remote.sudo("printf 'hello world'; printf \"hello world\"", { user: 'node' });

//  remote.sudo('cd /etc; ls -al', { user: 'node' });
});

plan.local(function(local) {
  local.sudo('echo hello world');
});

plan.local(function(local) {
  local.sudo('printf "hello world"');
  local.sudo("printf 'hello world'; printf \"hello world\"");

//  local.sudo('cd /etc; ls -al');
});
